import React, { useState } from 'react'

import './FormResep.css'

export default function FormResep() {
    const[formData, setFormData] = useState(
        {
            id : "",
            judulResep : "",
            image : "",
            deskripsi : ""
        }
    )

    const handleChange = (event) => {
    const {name, value} = event.target;
    setFormData((prevState) => ({
        ...prevState,
        [name]: value,
    }));
    };
    
    const handleSubmit = async (event) => {
        event.preventDefault();

    console.log(formData);
    try{
        const response = await fetch ('http://localhost:3001/formulirResep',{
            method: 'POST',
            headers:{
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        });
        const jsonData = await response.json();
        console.log(jsonData);
    }   catch(error){
        console.log(error);
    }
    };
    
    return (
        <div className='FormResepJudul'>
            <h1 className='Resep-Form'>
                Input Resep Makanan
            </h1>

            <form className='Form-tabel' onSubmit={handleSubmit} key={formData.id}>
                <div className="form-group">
                    <label
                        for="exampleInputJudulResep1">
                        Judul Resep
                    </label>
                    <input type="judulresep"
                        className="form-control ctrl"
                        id="exampleInputJudulResep1"
                        placeholder="Enter judul resep" 
                        value ={formData.judulResep}
                        onChange={handleChange}
                        />

                </div>

                <br />
                <div className="form-group">
                <label 
                htmlFor="formFile" 
                className="form-label">
                Image
                </label>
                        <input 
                            className="form-control ctrl" 
                            type="file" 
                            id="formFile"
                            accept="image/*"
                        />
                </div>

                <br/>
                <div className="form-group">
                    <label
                        for="exampleInputDeskripsi1">
                        Deskripsi
                    </label>
                    <input type="deskripsi"
                        className="form-control ctrl"
                        id="exampleInputDeskripsi1"
                        placeholder="Enter deskripsi" />
                </div>

                <br />
                <div className='input-form'>
                    <button type="input"
                        className="btn btn-primary ipt">
                        Input
                    </button>
                </div>
            </form>
        </div>
    )
}
