import React from 'react';

const MenuResep = ({ resep }) => {
  return (
    <div className="MenuResep">
      <h1>Menu Resep</h1>
      <div className="resep">
        <h2>Judul Resep: {resep.judulResep}</h2>
        <img src={resep.image} alt="Resep" />
        <p>Deskripsi: {resep.deskripsi}</p>
      </div>
    </div>
  );
};

export default MenuResep;




