import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

import './FormResep.css';

export default function FormResep() {
  const [formData, setFormData] = useState({
    id: "",
    judulResep: "",
    image: "",
    deskripsi: ""
  });
  const history = useHistory();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log(formData);
    try {
      const response = await fetch('http://localhost:3001/formulirResep', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const jsonData = await response.json();
      console.log(jsonData);

      // Redirect to menu resep
      history.push('/menu-resep');
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className='FormResepJudul'>
      <h1 className='Resep-Form'>
        Input Resep Makanan
      </h1>

      <form className='Form-tabel' onSubmit={handleSubmit} key={formData.id}>
        {/* Form fields */}
        {/* ... */}

        <br />
        <div className='input-form'>
          <button type="submit" className="btn btn-primary ipt">
            Input
          </button>
        </div>
      </form>
    </div>
  );
}
