import React, { useState } from 'react';
import axios from 'axios';

const Form = () => {
  const [judul, setJudul] = useState('');
  const [deskripsi, setDeskripsi] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('judul', judul);
    formData.append('deskripsi', deskripsi);
    formData.append('image', selectedImage);

    try {
      await axios.post('http://localhost:3001/api/formulirresep', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      alert('Data berhasil dikirim!');
      setJudul('');
      setDeskripsi('');
      setSelectedImage(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleBack = () => {
    window.history.back();
  };

  const handleImageChange = (e) => {
    setSelectedImage(e.target.files[0]);
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: '5%' }}>
      <button className="btn btn-secondary mb-2" onClick={handleBack}>
        Back
      </button>
      <div>
        <div className="mb-3" style={{ marginBottom: '100%', display: 'inline-flex' }}>
          <label htmlFor="judul" className="form-label" style={{ marginTop: '5%' }}>
            Judul:
          </label>
          <input
            className="form-control"
            type="text"
            id="judul"
            value={judul}
            onChange={(e) => setJudul(e.target.value)}
            required
          />
        </div>
      </div>
      <div className="mb-3">
        <label htmlFor="deskripsi" className="form-label">
          Deskripsi:
        </label>
        <textarea
          className="form-control"
          id="deskripsi"
          value={deskripsi}
          onChange={(e) => setDeskripsi(e.target.value)}
          required
        />
      </div>
      <div className="mb-3">
        <label htmlFor="image" className="form-label">
          Image:
        </label>
        <input
          className="form-control"
          type="file"
          id="image"
          accept="image/*"
          onChange={handleImageChange}
          required
        />
      </div>
      <button className="btn btn-primary" type="submit">
        Submit
      </button>
    </form>
  );
};

export default Form;





