import React, { useState, useEffect } from 'react';
import './FormResep.css';

export default function FormResep() {
  const [Data, setData] = useState({
    judulResep: "",
    image: "",
    deskripsi: ""
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log(formData);
    try {
      const response = await fetch('http://localhost:3001/formulirresep', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const jsonData = await response.json();
      console.log(jsonData);

      // Redirect or do something else after submitting data
      // ...
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://localhost:3001/formulirresep");
      const jsonData = await response.json();
      setData(jsonData);
    };

    fetchData();
  }, []);

  return (
    <div className='FormResepJudul'>
      <h1 className='Resep-Form'>
        Input Resep Makanan
      </h1>

      <form className='Form-tabel' onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="exampleInputJudulResep1">
            Judul Resep
          </label>
          <input
            type="text"
            className="form-control ctrl"
            id="exampleInputJudulResep1"
            placeholder="Enter judul resep"
            name='judulResep'
            value={formData.judulResep || ''}
            onChange={handleChange}
          />
        </div>

        <br />
        <div className="form-group">
          <label htmlFor="formFile" className="form-label">
            Image
          </label>
          <input
            className="form-control ctrl"
            type="file"
            id="formFile"
            accept="image/*"
            name='image'
            value={formData.image}
            onChange={handleChange}
          />
        </div>

        <br />
        <div className="form-group">
          <label htmlFor="exampleInputDeskripsi1">
            Deskripsi
          </label>
          <input
            type="text"
            className="form-control ctrl"
            id="exampleInputDeskripsi1"
            placeholder="Enter deskripsi"
            name='deskripsi'
            value={formData.deskripsi}
            onChange={handleChange}
          />
        </div>

        <br />
        <div className='input-form'>
          <button type="input" className="btn btn-primary ipt">
            Input
          </button>
        </div>
      </form>
    </div>
  )
}
